import { useState } from "react";
import axios from "axios";

const Register = () => {
    const [user, setUser] = useState({
        name:"",
        email:"",
        password:""
    });
    const handleChange = e => {
        const {name, value} = e.target;
        setUser({
            ...user,
            [name]:value
        });
    }

    //register func
    const register = () => {
        const {name, email, password} = user;
        if (name && email && password){
            try {
                axios.post("http://localhost:3001/register", user )
                .then(res => console.log(res));
            }catch (error){
                console.error(error.response.data);
            }
            
        }else{
            alert("err: invalid input");
        }
    }
    return(
        <div class="container">
            <div class="card">
                <div class="container">
                    <br/>
                    <h3 class="card-title">Create Account</h3>
                    <br/>
                    <form>
                        <div class="mb-3">
                            <label for="userInput" class="form-label">Email Address</label>
                            <input id="userInput" class="form-control" type="text" name="name" value={user.name} onChange={handleChange} placeholder="Name"/>
                        </div>
                        <div class="mb-3">

                        </div>
                        <div class="mb-3">

                        </div>
                    </form>
                </div>
                <span>
                    Already have an account?
                    <a href="/Login">
                        Login
                    </a>
                </span>
                <div>
                    <form action="#">
                        <div>
                            <div>
                                <input type="text" name="name" value={user.name} onChange={handleChange} placeholder="Name"/>
                            </div>
                        </div>
                        <div>
                            <div>
                                <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Email"/>
                            </div>
                        </div>
                        <div>
                            <div>
                                <input type="password" name="password" value={user.password} onChange={handleChange} placeholder="password"/>
                            </div>
                        </div>
                        <div>
                            <button type="submit" onClick={register}>
                                Register
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default Register;
